sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if ( newValue == '') return;

	//Type appropriate comment here, and begin script below
	var field = 'u_whats_needed';
	var field_default = g_form.getValue(field);
	g_form.clearOptions(field);

	switch( newValue ) {
		case 'hr':
			g_form.addOption(field, 'hr1', 'Human Resources 1');
			g_form.addOption(field, 'hr2', 'Human Resources 2');
			g_form.addOption(field, 'other', 'Other');
			break;
		case 'facilities':
			g_form.addOption(field, 'facilities1', 'Facilities 1');
			g_form.addOption(field, 'facilities2', 'Facilities 2');
			g_form.addOption(field, 'other', 'Other');
			break;
		case 'legal':
			g_form.addOption(field, 'legal1', 'Legal 1');
			g_form.addOption(field, 'legal2', 'Legal 2');
			g_form.addOption(field, 'other', 'Other');
			break;
		case 'your_choice':
			g_form.addOption(field, 'you_choice_1', 'Your Department Choice 1');
			g_form.addOption(field, 'you_choice_2', 'Your Department Choice 2');
			g_form.addOption(field, 'other', 'Other');
			break;
	}

	if(isLoading && !g_form.isNewRecord()) g_form.setValue(field, field_default);

}
